import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import LandingPage from './pages/LandingPage';
import ProductListingPage from './pages/ProductListingPage';
import ShoppingCartPage from './pages/ShoppingCartPage';
import './App.css';

function App() {
  const [products] = useState([
    {
      id: 1,
      name: 'Snake Plant',
      price: 25.99,
      description: 'An easy-care plant that is perfect for beginners. Known for its air-purifying qualities.',
      thumbnail: 'https://www.thespruce.com/thmb/CpEP-cPmDmz6kwdmVKbhcrdCJuY=/750x0/filters:no_upscale():max_bytes(150000):strip_icc():format(webp)/snake-plant-care-overview-1902772-04-d3990a1d0e1d4202a824e929abb12fc1-349b52d646f04f31962707a703b94298.jpeg', // Placeholder URL for image
      category: 'Air Purifying'
    },
    {
      id: 2,
      name: 'Aloe Vera',
      price: 15.99,
      description: 'A succulent plant that is known for its medicinal properties and ease of care.',
      thumbnail: 'data:image/jpeg;base64,', // Placeholder URL for image
      category: 'Medicinal'
    },
    {
      id: 3,
      name: 'Peace Lily',
      price: 30.00,
      description: 'A beautiful flowering plant that thrives in low light and purifies the air.',
      thumbnail: 'data:image/jpeg;base64,', // Placeholder URL for image
      category: 'Air Purifying'
    },
    {
      id: 4,
      name: 'Lavender',
      price: 20.00,
      description: 'A fragrant plant that is popular for its aromatic properties and beautiful purple flowers.',
      thumbnail: 'https://upload.wikimedia.org/wikipedia/commons/7/7e/Single_lavender_flower02.jpg', // Placeholder URL for image
      category: 'Aromatic'
    },
    {
      id: 5,
      name: 'Basil',
      price: 10.00,
      description: 'An aromatic herb that is easy to grow indoors and is a staple in many culinary dishes.',
      thumbnail: 'data:image/jpeg;base64,', // Placeholder URL for image
      category: 'Culinary'
    },
    {
      id: 6,
      name: 'Spider Plant',
      price: 18.50,
      description: 'A resilient plant that is known for its spider-like leaves and its ability to thrive in various conditions.',
      thumbnail: 'data:image/jpeg;base64,', // Placeholder URL for image
      category: 'Air Purifying'
    }
  ]);

  const [cartItems, setCartItems] = useState([]);

  const handleAddToCart = (product) => {
    setCartItems((prevCartItems) => {
      const existingItem = prevCartItems.find(item => item.id === product.id);
  
      if (existingItem) {
        return prevCartItems.map(item =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prevCartItems, { ...product, quantity: 1 }];
      }
    });
  };
  

  const handleIncrease = (item) => {
    setCartItems((prevCartItems) => 
      prevCartItems.map(cartItem => 
        cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity + 1 } : cartItem
      )
    );
  };
  
  const handleDecrease = (item) => {
    setCartItems((prevCartItems) => 
      prevCartItems.map(cartItem => 
        cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity - 1 } : cartItem
      ).filter(cartItem => cartItem.quantity > 0)
    );
  };
  
  const handleRemove = (item) => {
    setCartItems((prevCartItems) => 
      prevCartItems.filter(cartItem => cartItem.id !== item.id)
    );
  };
  

  return (
    <div className='App'>

    <Router>
    <Routes>
        <Route path="/" element={<LandingPage />} />
        <Route 
          path="/products" 
          element={<ProductListingPage products={products} onAddToCart={handleAddToCart} cartItems={cartItems} />} 
        />
        <Route 
          path="/cart" 
          element={<ShoppingCartPage cartItems={cartItems} onIncrease={handleIncrease} onDecrease={handleDecrease} onRemove={handleRemove} />} 
        />
      </Routes>
    </Router>        </div>

  );
}

export default App;
